import React, { useState } from 'react';
import { format } from 'date-fns';

export default function BookingForm({ onBookRoom }) {
  const [formData, setFormData] = useState({
    roomType: 'single',
    checkIn: format(new Date(), 'yyyy-MM-dd'),
    checkOut: format(new Date(Date.now() + 86400000), 'yyyy-MM-dd'),
    guestName: '',
    guestEmail: ''
  });
  const [availableRooms, setAvailableRooms] = useState(0);
  const [message, setMessage] = useState('');

  const checkAvailability = () => {
    if (!formData.checkIn || !formData.checkOut) {
      setMessage('Please select dates');
      return;
    }

    if (new Date(formData.checkIn) >= new Date(formData.checkOut)) {
      setMessage('Check-out must be after check-in');
      return;
    }

    // Simulate availability check
    const availableCount = formData.roomType === 'single' ? 
      Math.max(0, 7 - Math.floor(Math.random() * 4)) : // Random between 4-7 available
      Math.max(0, 3 - Math.floor(Math.random() * 2)); // Random between 1-3 available

    setAvailability({
      available: availableCount > 0,
      count: availableCount
    });

    setMessage(availableCount > 0 
      ? `${availableCount} ${formData.roomType} room(s) available!` 
      : `No ${formData.roomType} rooms available for these dates`
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (availableRooms === 0) {
      setMessage('No available rooms');
      return;
    }

    const result = onBookRoom(formData);
    setMessage(result.message);

    if (result.success) {
      setFormData({
        roomType: 'single',
        checkIn: format(new Date(), 'yyyy-MM-dd'),
        checkOut: format(new Date(Date.now() + 86400000), 'yyyy-MM-dd'),
        guestName: '',
        guestEmail: ''
      });
      setAvailableRooms(0);
    }
  };

  return (
    <div className="booking-form">
      <h2>Book a Room</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Room Type:</label>
          <select
            value={formData.roomType}
            onChange={e => setFormData({...formData, roomType: e.target.value})}
          >
            <option value="single">Single Room</option>
            <option value="double">Double Room</option>
          </select>
        </div>

        <div className="form-group">
          <label>Check-in:</label>
          <input
            type="date"
            value={formData.checkIn}
            min={format(new Date(), 'yyyy-MM-dd')}
            onChange={e => setFormData({...formData, checkIn: e.target.value})}
          />
        </div>

        <div className="form-group">
          <label>Check-out:</label>
          <input
            type="date"
            value={formData.checkOut}
            min={formData.checkIn}
            onChange={e => setFormData({...formData, checkOut: e.target.value})}
          />
        </div>

        <button 
          type="button" 
          className="btn check-btn"
          onClick={checkAvailability}
        >
          Check Availability
        </button>

        {availableRooms > 0 && (
          <div className="guest-details">
            <div className="form-group">
              <label>Guest Name:</label>
              <input
                type="text"
                value={formData.guestName}
                onChange={e => setFormData({...formData, guestName: e.target.value})}
                required
              />
            </div>

            <div className="form-group">
              <label>Guest Email:</label>
              <input
                type="email"
                value={formData.guestEmail}
                onChange={e => setFormData({...formData, guestEmail: e.target.value})}
                required
              />
            </div>

            <button type="submit" className="btn book-btn">
              Confirm Booking
            </button>
          </div>
        )}

        {message && <div className={`message ${message.includes('success') ? 'success' : ''}`}>{message}</div>}
      </form>
    </div>
  );
}
