import React, { useState } from 'react';
import { format } from 'date-fns';

export default function BookingForm({ onBookRoom, availability, selectedDate }) {
  const [formData, setFormData] = useState({
    roomType: 'single',
    checkIn: format(selectedDate, 'yyyy-MM-dd'),
    checkOut: format(new Date(selectedDate.getTime() + 86400000), 'yyyy-MM-dd'),
    guestName: '',
    guestEmail: '',
    roomNumber: null
  });

  const [message, setMessage] = useState('');
  const [availableRooms, setAvailableRooms] = useState([]);

  const checkAvailability = () => {
    if (!formData.checkIn || !formData.checkOut) {
      setMessage('Please select dates');
      return;
    }

    if (new Date(formData.checkIn) >= new Date(formData.checkOut)) {
      setMessage('Check-out must be after check-in');
      return;
    }

    const dateStr = format(selectedDate, 'yyyy-MM-dd');
    const roomsOfType = availability[formData.roomType].rooms;

    const available = roomsOfType.filter(room => 
      !room.isBooked
    );

    setAvailableRooms(available);
    setMessage(available.length > 0 
      ? `${available.length} ${formData.roomType} room(s) available!` 
      : `No ${formData.roomType} rooms available for these dates`
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!formData.roomNumber) {
      setMessage('Please select a room');
      return;
    }

    const result = onBookRoom(formData);
    setMessage(result.message);

    if (result.success) {
      setFormData({
        roomType: 'single',
        checkIn: format(selectedDate, 'yyyy-MM-dd'),
        checkOut: format(new Date(selectedDate.getTime() + 86400000), 'yyyy-MM-dd'),
        guestName: '',
        guestEmail: '',
        roomNumber: null
      });
      setAvailableRooms([]);
    }
  };

  return (
    <div className="booking-form">
      <h2>Book a Room</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Room Type:</label>
          <select
            value={formData.roomType}
            onChange={e => setFormData({
              ...formData, 
              roomType: e.target.value,
              roomNumber: null
            })}
          >
            <option value="single">Single Room</option>
            <option value="double">Double Room</option>
          </select>
        </div>

        <div className="form-group">
          <label>Check-in:</label>
          <input
            type="date"
            value={formData.checkIn}
            min={format(new Date(), 'yyyy-MM-dd')}
            onChange={e => setFormData({
              ...formData, 
              checkIn: e.target.value,
              roomNumber: null
            })}
          />
        </div>

        <div className="form-group">
          <label>Check-out:</label>
          <input
            type="date"
            value={formData.checkOut}
            min={formData.checkIn}
            onChange={e => setFormData({
              ...formData, 
              checkOut: e.target.value,
              roomNumber: null
            })}
          />
        </div>

        <button 
          type="button" 
          className="btn check-btn"
          onClick={checkAvailability}
        >
          Check Availability
        </button>

        {availableRooms.length > 0 && (
          <div className="room-selection">
            <h4>Available Rooms:</h4>
            <div className="room-options">
              {availableRooms.map(room => (
                <button
                  type="button"
                  key={room.number}
                  className={`room-btn ${formData.roomNumber === room.number ? 'selected' : ''}`}
                  onClick={() => setFormData({
                    ...formData,
                    roomNumber: room.number
                  })}
                >
                  Room {room.number}
                </button>
              ))}
            </div>
          </div>
        )}

        {formData.roomNumber && (
          <div className="guest-details">
            <div className="form-group">
              <label>Guest Name:</label>
              <input
                type="text"
                value={formData.guestName}
                onChange={e => setFormData({
                  ...formData, 
                  guestName: e.target.value
                })}
                required
              />
            </div>

            <div className="form-group">
              <label>Guest Email:</label>
              <input
                type="email"
                value={formData.guestEmail}
                onChange={e => setFormData({
                  ...formData, 
                  guestEmail: e.target.value
                })}
                required
              />
            </div>

            <button type="submit" className="btn book-btn">
              Book Room {formData.roomNumber}
            </button>
          </div>
        )}

        {message && (
          <div className={`message ${message.includes('success') ? 'success' : ''}`}>
            {message}
          </div>
        )}
      </form>
    </div>
  );
}
